/* LINTLIBRARY */

#include "sr_conf.h"

#include <stdio.h>
#include "sr_proto.h"
#include "general.h"
#include "manifest.h"

/*  */

char           *
sprintb(v, bits)
    register int    v;
    register char  *bits;
{
    register int    i, j;
    register char   c, *bp;
    static char     buffer[BUFSIZ];

    (void) sprintf(buffer, bits && *bits == 010 ? "0%o" : "0x%x", v);
    bp = buffer + strlen(buffer);

    if (bits && *++bits) {
	j = 0;
	*bp++ = '<';
	while ((i = *bits++))
	    if (v & (1 << (i - 1))) {
		if (j++)
		    *bp++ = ',';
		for (; (c = *bits) > 32; bits++)
		    *bp++ = c;
	    }
	    else
		for (; *bits > 32; bits++)
		    continue;
	*bp++ = '>';
	*bp = '\0';
    }

    return buffer;
}
