shr_error_e
dnx_data_bfd_init(
    int unit,
    dnxc_data_module_t *module_data)
{
    SHR_FUNC_INIT_VARS(unit);

    /* Data Struct Init */
    module_data->name = "bfd";
    module_data->nof_submodules = _dnx_data_bfd_submodule_nof;
    DNXC_DATA_ALLOC(module_data->submodules, dnxc_data_submodule_t, module_data->nof_submodules, "_dnxc_data bfd submodules");

    /*
     * Init Submodule data
     */
    SHR_IF_ERR_EXIT(dnx_data_bfd_general_init(unit, &module_data->submodules[dnx_data_bfd_submodule_general]));
    SHR_IF_ERR_EXIT(dnx_data_bfd_hw_bug_init(unit, &module_data->submodules[dnx_data_bfd_submodule_hw_bug]));
    SHR_IF_ERR_EXIT(dnx_data_bfd_property_init(unit, &module_data->submodules[dnx_data_bfd_submodule_property]));
    /*
     * Attach device module
     */
    if (dnxc_data_mgmt_is_jer2_a0(unit))
    {
        SHR_IF_ERR_EXIT(jer2_a0_data_bfd_attach(unit));
    }
    else
    if (dnxc_data_mgmt_is_jer2_b0(unit))
    {
        SHR_IF_ERR_EXIT(jer2_a0_data_bfd_attach(unit));
        SHR_IF_ERR_EXIT(jer2_b0_data_bfd_attach(unit));
    }

exit:
    SHR_FUNC_EXIT;
}
